import React, { Component } from 'react';

class CreateStaffComponent extends Component {
    constructor(props){
        super(props)
        this.setState = {

            firstname: '',
            lastname: '',
            email: '',
            address: '',
            phone: ''

        }

        // this.changeAddressHandler = this.changeAddressHandler.bind(this);
        // this.changeEmailHandler = this.changeEmailHandler.bind(this);
        // this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        // this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        // this.changePhoneHandler = this.changePhoneHandler.bind(this);
        // this.saveStaff = this.saveStaff.bind(this);

    }

    saveStaff = (e) => {
        e.preventDefault();

        let staff = {firstname: this.state.firstname,
        lastname: this.state.lastname,
        email: this.state.email,
        address: this.state.address,
        phone: this.state.phone}

        console.log('staff => ' + JSON.stringify(staff));
    }


    changeFirstNameHandler = (event) => {
        this.setState({firstname: event.target.value});
    }

    changeLastNameHandler = (event) => {
        this.setState({lastname: event.target.value})
    }

    changeEmailHandler = (event) => {
        this.setState({email: event.target.value})
    }

    changeAddressHandler = (event) => {
        this.setState({address: event.target.value})
    }

    changePhoneHandler = (event) => {
        this.setState({address : event.target.value})
    }

    cancel() {
        this.props.history.push('/staffs');
    }


    render() {
        return (
            <div>
                <div className = "container">
                    <div className = "row">
                        <div className= "card col-md-6 offset-md-3 offset-md-3">
                            <h3 className= "text-center"> Add Staff </h3>
                                <div className= "card-body">
                                    <form>
                                        <div className= "form-group">
                                            <label> First Name: </label>
                                            <input placeholder="First Name" name ="firstname" className="form-control" value={this.state.firstname} onChange={this.changeFirstNameHandler}/>
                                        </div>
                                        <div className= "form-group">
                                            <label> Last Name: </label>
                                            <input placeholder="Last Name" name = "lastname" className="form-control" value={this.state.lastname} onChange={this.changeLastNameHandler}/>
                                        </div>
                                        <div className= "form-group">
                                            <label> Email: </label>
                                            <input placeholder="Email" name = "email" className="form-control" value={this.state.email} onChange={this.changeEmailHandler}/>
                                        </div>
                                        {/* <div className= "form-group">
                                            <label> Address: </label>
                                            <input placeholder="Address" name = "address" className="form-control" value={this.state.address} onChange={this.changeAddressHandler}/>
                                        </div>
                                        <div className= "form-group">
                                            <label> Phone: </label>
                                            <input placeholder="Phone" name = "phone" className="form-control" value={this.state.phone} onChange={this.changePhoneHandler}/>
                                        </div> */}

                                        <button className="btn btn-sucess" onClick={this.saveStaff}> Save </button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}> Delete </button>

                                    </form>
                                </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateStaffComponent;
